                    <div onClick={()=>{setKol(21)}} className={`${kol === 21 ? 'bg-slate-200':'bg-slate-700'} i w-[30%] h-[30%] rounded-sm hover:shadow-sfirst2`} />
                    <div onClick={()=>{setKol(22)}} className={`${kol === 22 ? 'bg-slate-200':'bg-slate-700'} i w-[30%] h-[30%] rounded-sm hover:shadow-sfirst2`} />
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Experience;
